new_data_grid=map_dfr(1, ~data.frame(unique(test[,c("id","condit","bandInt")]))) |&gt; 
  dplyr::arrange(id,bandInt) |&gt; 
  mutate(condit_dummy = ifelse(condit == "Varied", 1, 0)) 

indv_coefs &lt;- coef(e1_vxBMM)$id |&gt; 
  as_tibble(rownames="id") |&gt; 
  select(id, starts_with("Esti")) |&gt;
  left_join(e1Sbjs, by=join_by(id))

indv_coefs_preds &lt;- new_data_grid |&gt; left_join(indv_coefs,by=join_by(id,condit,condit_dummy)) |&gt;
  mutate(
    estimate = (Estimate.Intercept) + 
      (bandInt*(Estimate.bandInt)) + 
      (condit_dummy*Estimate.conditVaried) +
      (bandInt * condit_dummy) * `Estimate.conditVaried:bandInt`)

