e1_vxBMM %&gt;% as_draws_df(variable = c('^b_', 'sigma'), regex = TRUE) |&gt;
  select(starts_with("b_"),sigma) |&gt; 
  pivot_longer(
    everything(),
    names_to = 'key',
    values_to = 'value') %&gt;% 
  ggplot(aes(x = value, fill = key)) +
  stat_halfeye(
    # plot the mode
    point_interval = mode_hdi,
    # plot HDIs be at both the 95% levels
    .width = c(.95),
    outline_bars = TRUE,
    normalize = 'panels',
    show.legend = FALSE
  ) +
  facet_wrap(~key, scales = 'free', ncol = 4) +
  ggtitle('Posterior distributions')



